import React from 'react';
import {
  makeStyles,
} from '@material-ui/core';
import { Formik, Form } from 'formik';
import * as yup from 'yup';
import { UPDATE } from 'queries';
import { useMutation } from '@apollo/client';
import { User } from 'types';
import { UseUserProps } from 'hooks/user';
import { useRouter } from 'next/router';

import InputFied from '../../InputField';
import SettingWrapper from '../../SettingWrapper';
import UpdateButton from '../../UpdateButton';

const useStyles = makeStyles((theme) => ({
  containerInput: {
    display: 'flex',
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'space-between',
    alignItems: 'flex-start',
    minHeight: '100px',
    height: '100px',
    maxHeight: '100px',
    [theme.breakpoints.down('xs')]: {
      flex: 1,
      width: '100%',
      maxHeight: '300px',
      marginBottom: theme.spacing(1),
    },
  },
  styleForm: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    flex: 1,
    width: '100%',
    [theme.breakpoints.down('xs')]: {
      flexDirection: 'column',
    },
  },
}));

interface UpdateUserParams {
  newUsername?: string;
  email?: string;
  description?: string;
}

const initialValues: UpdateUserParams = {
  newUsername: '', email: '', description: '',
};

const testSchema = yup.object().shape({
});

function useUpdate({ id }: UseUserProps): (variables: UpdateUserParams) => Promise<void> {
  const [update] = useMutation<{ update: User }>(UPDATE, {
    variables: { id },
    onCompleted: () => {
      console.log('success');
    },

  });
  return (async (variables: UpdateUserParams): Promise<void> => {
    await update({ variables });
  });
}

function Biography(): JSX.Element {
  const classes = useStyles();
  const router = useRouter();
  const update = useUpdate(({ id: String(router.query.params) }));
  console.log(update(initialValues));
  return (
    <SettingWrapper
      title="Description :"
    >
      <Formik
        validationSchema={testSchema}
        initialValues={initialValues}
        onSubmit={(values: UpdateUserParams): void => {
          console.log('description', values);
          // update(values);
        }}
      >
        <div className={classes.containerInput}>
          <Form className={classes.styleForm}>
            <InputFied
              name="description"
              label="Description :"
              value="Software developer"
              rows={4}
              multiline
            />
            <UpdateButton description="Update your description" onClick={(): void => (console.log('save'))} />
          </Form>
        </div>
      </Formik>
    </SettingWrapper>
  );
}

export default Biography;
