import React from 'react';
import {
  makeStyles,
} from '@material-ui/core';
import { Formik, Form } from 'formik';
import * as yup from 'yup';
import { UPDATE, USER } from 'queries';
import { useMutation } from '@apollo/client';
import { User } from 'types';

import InputFied from '../../InputField';
import SettingWrapper from '../../SettingWrapper';
import UpdateButton from '../../UpdateButton';

const useStyles = makeStyles((theme) => ({
  containerInput: {
    display: 'flex',
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'space-between',
    alignItems: 'flex-start',
    minHeight: '100px',
    height: '100px',
    maxHeight: '100px',
    [theme.breakpoints.down('xs')]: {
      flex: 1,
      width: '100%',
      maxHeight: '300px',
      marginBottom: theme.spacing(1),
    },
  },
  styleForm: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    flex: 1,
    width: '100%',
    [theme.breakpoints.down('xs')]: {
      flexDirection: 'column',
    },
  },
}));

interface UpdateUserParams {
  id: string;
  newUsername: string;
  email: string;
  description: string;
}

const initialValues: UpdateUserParams = {
  id: '', newUsername: '', email: '', description: '',
};

const testSchema = yup.object().shape({
});

function useUpdate(): (variables: UpdateUserParams) => Promise<void> {
  const [update] = useMutation<{ update: User }>(UPDATE, {
    onCompleted: () => {
      console.log('success');
    },
    update: (cache, { data: { update: user } }) => {
      cache.writeQuery({
        query: USER,
        data: { user },
      });
    },

  });
  return (async (variables: UpdateUserParams): Promise<void> => {
    await update({ variables });
  });
}

function Biography(): JSX.Element {
  const classes = useStyles();
  const update = useUpdate();

  return (
    <SettingWrapper
      title="Biography :"
    >
      <Formik
        validationSchema={testSchema}
        initialValues={initialValues}
        onSubmit={(values: UpdateUserParams): void => {
          console.log('biography', values);
          update(values);
        }}
      >
        <div className={classes.containerInput}>
          <Form className={classes.styleForm}>
            <InputFied
              name="biography"
              label="Biography :"
              value="Software developer"
              rows={4}
              multiline
            />
            <UpdateButton description="Update your biography" onClick={(): void => (console.log('save'))} />
          </Form>
        </div>
      </Formik>
    </SettingWrapper>
  );
}

export default Biography;
