import React from 'react';
import {
  makeStyles,
} from '@material-ui/core';
import { Formik, Form } from 'formik';
import * as yup from 'yup';
import { UPDATE } from 'queries';
import { useMutation } from '@apollo/client';
import { useRouter } from 'next/router';
import { useUser } from 'hooks/user';
import ErrorPage from 'next/error';

import Layout from 'components/Layout';

import InputFied from '../../InputField';
import SettingWrapper from '../../SettingWrapper';
import UpdateButton from '../../UpdateButton';

const useStyles = makeStyles((theme) => ({
  containerInput: {
    display: 'flex',
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'space-between',
    alignItems: 'flex-start',
    minHeight: '100px',
    height: '100px',
    maxHeight: '100px',
    [theme.breakpoints.down('xs')]: {
      flex: 1,
      width: '100%',
      maxHeight: '300px',
      marginBottom: theme.spacing(1),
    },
  },
  styleForm: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    flex: 1,
    width: '100%',
    [theme.breakpoints.down('xs')]: {
      flexDirection: 'column',
    },
  },
}));

interface UpdateUserParams {
  newUsername?: string;
  email?: string;
  description?: string;
}

const testSchema = yup.object().shape({
});

const initialValues: UpdateUserParams = {
  newUsername: '', email: '', description: '',
};

function Biography(): JSX.Element {
  const classes = useStyles();
  // const router = useRouter();
  const [update] = useMutation(UPDATE);
  // const user = useUser({ id: String(router.query.params) });
  // console.log('id', user.id);

  return (
    <SettingWrapper
      title="Description :"
    >
      <Formik
        validationSchema={testSchema}
        initialValues={initialValues}
        onSubmit={(values: UpdateUserParams): void => {
          update({
            variables: {
              id: 'ckh6whm390001eyt8mg7ja97m',
              description: values.description,
            },
          });
        }}
      >
        <div className={classes.containerInput}>
          <Form className={classes.styleForm}>
            <InputFied
              name="description"
              label="Description :"
              value="Software developer"
              rows={4}
              multiline
            />
            <UpdateButton description="Update your description" onClick={(): void => (console.log('save'))} />
          </Form>
        </div>
      </Formik>
    </SettingWrapper>
  );
}

export default Biography;
